function out = doentry(DATA, Clusters, id)type = DATA.plot.onetype;done = 0;DATA.currentpoint(1) = id;if DATA.plot.allxy    PC.PlotAllProbeXY(DATA);    done = 1;endif DATA.plot.quickspks    PC.PlotAllProbe(DATA, 'allspks');    done = 1;endif done    PC.PlotCellList(DATA);    out = DATA;    return;endfigure(DATA.fig.clusters);if strcmp(type,'cov(shape)')    PC.CompareProbesShape(DATA,id,1);elseif strcmp(type,'xc(shape)')    PC.CompareProbesShape(DATA,id,2);elseif strcmp(type,'xy')    cres = PlotSpikeTimes(Clusters{id},'xy');else    cres = PlotSpikeTimes(Clusters{id},type,'hitfcn',@PC.HitPoint,DATA);endif strcmp(type,'xcorr')     PC.SetFigure(DATA,DATA.tag.xcorr);     imagesc(squeeze(cres.synci(:,:,1)));     DATA.synic{id} = cres;     xc = cres;     ofile = [DATA.name '/' strrep(DATA.strings{id},'ClusterTimes','Xcorrs')];     save(ofile,'xc');endout = DATA;