function h= testDrawLine(E,varargin)        if ~isfield(E,'pos') || isfield(E,'crit')  %pos can get out of data when save from plotclusters            xy= xyrotate([E.crit E.crit],get(gca,'ylim'),-E.angle);            E.pos = [xy(1,1), xy(1,2) xy(2,1) xy(2,2);];        endx = [E.pos(1) E.pos(3)];y = [E.pos(2) E.pos(4)];[x,y] = PC.Data2Norm(x,y);if length(E.pos) > 7    xp = E.pos([5 7]);    yp = E.pos([6 8]);elsexp = [mean(x) + diff(y)/4 mean(x)-diff(y)/4];yp = [mean(y) - diff(x)/4 mean(y)+diff(x)/4];endif isfield(E,'lh') & ishandle(E.lh(1))     set(E.lh(1),'Position',[x(1) y(1) x(2) y(2)]);    h = E.lh;    if length(E.h) > 1 && ishandle(E.h(2))        set(E.h(2),'Xdata',xp,'Ydata',yp);    endelse    hold on;    h(1) = annotation('line',real(x),real(y),varargin{:});    %h(2) = plot(xp,yp,'r:');    hold off;end