function [d, drop, C] = DistanceMeasure(C, type, varargin)    zeropad = 0;    j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'zeropad',6)            zeropad = 1;        end        j = j+1;    end        ok = 1;    drop = NaN;    if ok == 0        d = NaN;    elseif strncmp(type,'mahal',4)            d = C.mahal(type);    elseif strncmp(type,'fitdprime',5)        if isfield(C,'fitdprime')            d  = -C.fitdprime(1);        else            d = NaN;        end    elseif strncmp(type,'isolation',5)        if isfield(C,'isolation')            d  = mean(C.isolation);        else            d = NaN;        end    end    if isfield(C,'dropi') && length(C.dropi) > 2        drop = C.dropi(3);    end    if zeropad && isnan(d)        d = 0;    end                