function AddCellMean(DATA, cellid)        if isempty(cellid) || sum(cellid) ==0        return;    endfor c = 1:length(cellid)    if ~CellIsEmpty(DATA.CellDetails,'MeanSpike', cellid(c))    Ms = DATA.CellDetails.MeanSpike{cellid(c)};    voff = DATA.voffset(DATA.chspk)-DATA.voffset(AllV.ProbeNumber(DATA));    hold on;    for j = 1:size(Ms.ms,1);        plot(Ms.ms(j,:)+voff(j),'k-','linewidth',2);    endend end