function DATA = AddErr(DATA,varargin)    if isobject(varargin{1})        ERR = varargin{1};        varargin = varargin(2:end);    else        ERR = [];    ends = sprintf(varargin{:});mycprintf('errors','%s\n',s);if isfield(DATA,'errs')    DATA.nerr = length(DATA.errs)+1;else    DATA.nerr = 1;endDATA.errs{DATA.nerr} = s;DATA.errobj{DATA.nerr} = ERR;if isfield(DATA,'logfid') && DATA.logfid > 0    try        fprintf(DATA.logfid,'%s\r\n',s);    catch        fprintf('fid %d no longer valid\n',DATA.logfid);    endend