function AddParameterMenu(F, callback, tag)    DATA = GetDataFromFig(F);    hm = uimenu(F,'label',tag);    for j = 1:8        s = sprintf('PC%d',j);        uimenu(hm,'Label',s,'callback',{callback, tag, s});    end    for j = 1:length(DATA.TemplateLabels)        s = DATA.TemplateLabels{j};        uimenu(hm,'Label',s,'callback',{callback, tag, s});    end    strs = {'energy' 'spkvar' 'ADC1' 'ADC2' 'ADC1dvdt' 'ADC2dvdt' 'Centroid' 'CentroidSplit'};    for j = 1:length(strs)        uimenu(hm,'Label',strs{j},'callback',{callback, tag, strs{j}});    end        