function ApplyLayout(DATA,varargin)        j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'print',4)            fprintf('Applying %s\n',DATA.layoutfile);        end        j = j+1;    end        if ~exist(DATA.layoutfile)        fprintf('Cant read %s\n',DATA.layoutfile);        return;    end    DATA.layoutfile = CheckNameBug(DATA.layoutfile);    try %in case samba barfs        load(DATA.layoutfile);        setappdata(DATA.toplevel,'Figpos',Figpos);        f = fields(Figpos);        for j = 1:length(f)            it = findobj('type','figure','tag',f{j});            if length(it) == 1                set(it,'Position',Figpos.(f{j}));            end        end    end        