function [AllVoltages, DATA] = BuildAllV(DATA, id, spts)    V = AllV.mygetappdata(DATA,'Vall');    allid = repmat(id,length(spts),1) + repmat(spts',1,length(id));    AllVoltages = reshape(V.V(:,allid),[size(V.V,1) size(allid)]);    if isinteger(AllVoltages)        AllVoltages = double(AllVoltages) .* V.intscale(1)/V.intscale(2);    end    DATA = AllV.mysetappdata(DATA,'AllVoltages',AllVoltages);    if isfield(V,'meanV')        DATA.meanV = V.meanV(allid);        DATA = AllV.mysetappdata(DATA,'MeanV',DATA.meanV);    end    DATA.energy = [];    DATA.spkvar = [];    DATA.energy(1,:) = sqrt(squeeze(sum(diff(AllVoltages(DATA.probe(1),:,:),1,2).^2,2)));    DATA.spkvar(DATA.probe(1),:) = squeeze(std(AllVoltages(DATA.probe(1),:,:),[],2));    DATA.nevents = size(AllVoltages,3);