function DATA = CalcTemplateScores(DATA,  varargin)    if  isfield(DATA.cluster,'TemplateUsed')        MeanSpike.ms = DATA.cluster.TemplateUsed;        if size(DATA.cluster.mumeanUsed,1) == length(DATA.cluster.chspk)        MeanSpike.mu(DATA.cluster.chspk,:) = DATA.cluster.mumeanUsed;        else            MeanSpike.mu = DATA.cluster.mumeanUsed;        end        MeanSpike.vdprime(DATA.cluster.chspk,:) = DATA.cluster.DprimeUsed;    else        MeanSpike = DATA.cluster.MeanSpike;    end    DATA = AllV.CalcTemplatesFromMean(DATA, MeanSpike, varargin{:});