    function  sgn = CheckSign(C, x, energy)        e(1) = mean(energy(find(x > C.crit)));        e(2) = mean(energy(find(x < C.crit)));        sgn = 0;    if e(2) > 2 * e(1)        sgn = -1;    elseif e(1) > 2 * e(2)        sgn = 1;    end    if sgn == 0        if mean(x) < 0 && skewness(x) < 0            sgn = -1;        else            sgn = 1;        end    end            