function clusterplot = GetClusterPlot(DATA,E,plots, pt)    clusterplot = 0;     spaces = [1 2 3 3 5 6 7 8 9 10];    if ~isfield(E,'pcplot') || isempty(E.pcplot) || E.shape == 2 || length(E.pcplot) < 2        clusterplot = 0;    elseif DATA.plottype == 2 && length(E.pcplot) == 4 && sum(DATA.vpts(pt,:) == E.pcplot) == 4        clusterplot = pt;    elseif ismember(DATA.plottype,[1 3 4]) && sum(plots(pt,:) == E.pcplot(1:size(plots,2))) == 2 ...        && spaces(DATA.plottype) == E.space(1)        clusterplot = pt;    end    if isfield(E,'space') && length(E.space) > 2 && E.space(1) == DATA.plottype        if E.space(1) == 2 && sum(DATA.vsmps(plots(pt,:)) == E.space([3 5])) == 2            if DATA.vpts(pt,3) == E.space(4) %check probe too                clusterplot= pt;            end        elseif E.space(1) ~= 2 && sum(plots(pt,:) == E.space(2:3)) == 2            clusterplot= pt;        end    end