function [DATA, ClusterDetails] = LoadClusterDetails(DATA, varargin)    force = 0;    ClusterDetails = {};    j = 1;    while j <= length(varargin)        if strcmpi(varargin{j},'reload')            force =1;        end        j = j+1;    end        if DATA.interactive >= 0 && DATA.loadedClusterDetails == DATA.exptno && force == 0        ClusterDetails = AllV.mygetappdata(DATA,'ClusterDetails');        if ~isempty(ClusterDetails)        return;        end    end    if strcmp(DATA.cutmode,'auto')        cfile = AllV.ClusterFile(DATA.name,DATA.Expt,'details','auto');    else        cfile = AllV.ClusterFile(DATA.name,DATA.Expt,'details');    end    if exist(cfile)        load(cfile);        if ~isempty(ClusterDetails)            DATA = AllV.mysetappdata(DATA,'ClusterDetails',ClusterDetails);            DATA.loadedClusterDetails = DATA.exptno;        else %%may want to set loadedClusterDetails to 0?        end    end        