function cells = PlotAllXCorr(DATA, DataClusters, cells, varargin)    AllV.SetFigure(DATA.tag.xcorr,DATA);    ClearPlot;    setappdata(DATA.toplevel,'xcCellList',cells);    xpts = linspace(DATA.spts(1),DATA.spts(end),401);    nc = length(cells);    nxc = 0;    for j = 1:length(cells)    for k = 1:j        mysubplot(nc,nc,(j-1) * nc+k);        xoff = floor((j-1)/6) .* length(DATA.spts);        yoff = rem(j-1,6) .* DATA.vsep;        P = DataClusters{cells(j).p};        if cells(j).cl > 1            P  = P.next{cells(j).cl-1};        end        Q = DataClusters{cells(k).p};        if cells(k).cl > 1            Q  = Q.next{cells(k).cl-1};        end        [xc, details] = xcorrtimes(P.times,Q.times);        nxc = nxc+1;        xcorrs(nxc).shapexc = AllV.ShapeCorr(P,Q);        xcorrs(nxc).efficacy = details.efficacy;        if (j == k)            xc(details.midpt) = 0;        end        xcorrs(nxc).xc = xc;        xcorrs(nxc).p = [j k];        h = plot(xpts+xoff,yoff+(xc.*DATA.vsep./max(xc)),'k','linewidth',2);        [a,b]= max(xc);        if max(details.efficacy) > 0.2            set(h,'color','r');        end        set(gca,'xtick',[],'ytick',[],'buttondownfcn',{@AllV.PlotXcorr, j,k});        set(h,'buttondownfcn',{@AllV.PlotXcorr, j,k});        axis('tight');        drawnow;        if k == j;            if isfield(P,'fitdprime') && P.fitdprime(1) < -2                set(h,'color','r');            else                set(h,'color','k');            end            xl = get(gca,'xlim');            yl = get(gca,'ylim');            h = text(xl(2),yl(2),sprintf('%d/%d',cells(j).p,cells(j).cl),...                'horizontalalignment','right',...                'verticalalignment','bottom');            if j == 1                set(h,'horizontalalignment','right',...                'verticalalignment','bottom');            end        end    end    end    setappdata(DATA.toplevel,'xcorrs',xcorrs);    AllV.ReplotXcorrs(DATA, [], 'Shape/Efficacy')    