function [Vall, id] = PlotFullV(DATA, t, varargin)    addmean = 0;    dohold = 0;    color = 'k';    j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'addmean',7)            addmean = 1;        elseif strncmpi(varargin{j},'color',5)            j = j+1;            color = varargin{j};        elseif strncmpi(varargin{j},'hold',4)            dohold = 1;        end        j = j+1;    end    Vall = AllV.mygetappdata(DATA,'Vall');    if size(Vall.V,1) > 1        voff = DATA.voffset- DATA.voffset(AllV.ProbeNumber(DATA));    else        voff = 0;    end    id = find(Vall.t > t(1)  & Vall.t < t(2));    chspk = AllV.UseProbeList(DATA,DATA.plotspk.nfullvprobes);    if isfield(Vall,'intscale') && isinteger(Vall.V)        vscale = Vall.intscale(1)./Vall.intscale(2);    else        vscale = 1;    end    if dohold        hold on;    else        hold off;    end    for p = 1:length(chspk)        V = double(Vall.V(chspk(p),id)).*vscale+voff(chspk(p));        if addmean && isfield(Vall,'meanV')            V = V + Vall.meanV(id);        end        plot(Vall.t(id),V,'color',color);        hold on;    end    axis('tight');        