function PlotOneTrial(DATA,id)[DATA.currenttrial, DATA.spklst] = AllV.PlotTrialSpikes(DATA,id,'showall'); T = DATA.Expt.Trials(DATA.currenttrial);c = AllV.TrialMarkChar(T);fprintf('Trial %d%c: %.2f-%.2f\n',DATA.currenttrial,c,T.Start(1)./10000,T.End(end)./10000);set(DATA.toplevel,'UserData',DATA);    