function PlotOneXY(DATA,names)     if length(names) < 2        return;    end    x = AllV.GetValues(DATA,names{1});    y = AllV.GetValues(DATA,names{2});    if DATA.xyplot.density        args = {'density'};    else        args = {};    end    if length(x) == length(y)        GetFigure(DATA.tag.tmplscore);        PlotND([x y],[],'idlist',DATA.clst,'colors',DATA.colors,args{:});    else        cprintf('red','length mismatch %d vs %d\n',length(x),length(y));    end    xlabel(names{1});    ylabel(names{2});    