function PlotQuickSpikes(DATA, nspk, varargin)    if strncmp(DATA.DataType,'Grid',4)        AllV.PlotGridSpikes(DATA, nspk, varargin{:});        return;    end    DataClusters = AllV.mygetappdata(DATA,'Clusters');    npts = length(DATA.spts);    Vall = AllV.mygetappdata(DATA,'Vall');    probes = 1:size(Vall.V,1);    DATA.handles = ones(DATA.nprobes,2).*-1;    subplot('position',[0.01 0.01 0.98 0.98]);    hold off;    for p = probes        step = ceil(length(DATA.trigtimes{p})./nspk);        spklst = 1:step:length(DATA.trigtimes{p});        xoff = floor((p-1)/6) .* npts;        yoff = (rem(p-1,6)) * DATA.vsep;        AllV.PlotProbeSpikes(DATA, Vall, p, DATA.trigtimes{p}(spklst),[npts 8],[xoff yoff]);        text(xoff,yoff+1,sprintf('%d:%.1f',p,DataClusters{p}.mahal(1)),'fontsize',DATA.gui.fontsize(1));    end    set(gca,'ylim',[-2 5 .* DATA.vsep+2],'xtick',[],'ytick',[]);    set(gca,'xlim',[-8 length(DATA.spts)-8+xoff]);    set(gca,'ButtonDownFcn',@AllV.SpikeButtonPressed);