function PlotResult(a, b, type)     onoff = {'off' 'on'};     DATA = GetDataFromFig(a);     if strcmp(type,'plotexpt')         DATA.Expt = AllV.PlotExptCounts(DATA);         plotexpt = 1;     else         DATA.plot.expt = ~DATA.plot.expt;         set(a,'Checked',onoff{DATA.plot.expt+1});         plotexpt = DATA.plot.expt;     end     if plotexpt         DATA.Expt = AllV.PlotExptCounts(DATA);     end     set(DATA.toplevel,'UserData',DATA);          