function PlotTemplateScores(DATA, TemplateScores, probes)p = 3;if length(probes) > 1p = 2;slope = mean(squeeze(TemplateScores(p,1,:)))./mean(squeeze(TemplateScores(p,2,:)));diffs = squeeze(TemplateScores(p,1,:) - slope.* TemplateScores(p,2,:));hold off;p = 1;plot(squeeze(TemplateScores(p,1,DATA.nid)),diffs(DATA.nid),'.','markersize',1)hold on;plot(squeeze(TemplateScores(p,1,DATA.clid)),diffs(DATA.clid),'r.','markersize',1)hold off;end