function [DATA, V] = ReadSpikeFiles(DATA, name)    V.name = name;    id = regexp(V.name,'Expt[0-9]*');    DATA.exptno = sscanf(V.name(id+4:end),'%d');    DATA.Expt.exptno = DATA.exptno;    s = AllV.SpkFileName(DATA);    V.Spikes = ReadSpikeFile(s,'allprobes');    V.exptno = DATA.exptno;    [monk, monkey, mdir] = GetMonkeyName(DATA.name);    if V.Spikes.Header.bysuffix == 0    if isfield(V.Spikes,'matfile')        V.matfile = V.Spikes.matfile;    else        [a,b] = fileparts(DATA.name);        V.matfile = [a '/' monk mdir '.mat'];    end    end