function DATA = RunGMMFit(DATA,C, varargin)        Sf = DATA.spikefeatures(:,1:4);    replot = 0;    j = 1;    while j <= length(varargin)        if strncmpi(varargin{j},'replot',4)            replot = 1;        elseif strncmpi(varargin{j},'space',4)            j = j+1;            Sf = varargin{j};        end        j = j+1;    end            Spikes = AllV.MakeJamesSpikes(DATA);%    AllVoltages = AllV.mygetappdata(DATA,'AllVoltages');    [new_cluster.gmm_fit, new_cluster.dprime, new_cluster.comp_idx, new_cluster.cluster_labels, new_cluster.cluster_stats, outliers] = ...        GMM_fit(Spikes.V, Sf, C.Ncomps, C.params, C.Ncomps,C.comp_idx,C.cluster_labels);    C = CopyFields(C,new_cluster);    DATA.cluster.jamescluster = C;    DATA.gmcid = C.comp_idx;    if replot        DATA = AllV.ReplotPCs(DATA,[]);    end    