function ShowTaggedProbes(DATA)    if ~isfield(DATA,'TaggedProbes') || sum(DATA.TaggedProbes) == 0 || DATA.interactive <1        return;    end    tmenu = findobj(DATA.toplevel,'Tag','ProbeSwitchMenu');    c = get(tmenu,'Children');    id = find(DATA.TaggedProbes > 0);    for j = 1:length(id)        set(c(id(j)),'foregroundcolor',DATA.colors{DATA.TaggedProbes(id(j))});    end    