function XYplot(a,b,tag,fcn)    DATA = GetDataFromFig(a);onoff = {'off' 'on'};        if strcmp(tag,'density')        DATA.xyplot.density = ~DATA.xyplot.density;        set(a,'Checked',onoff{1+DATA.xyplot.density});        set(DATA.toplevel,'UserData',DATA);        AllV.PlotOneXY(DATA,DATA.xyplot.xy);        return;    end    xydim = find(strcmp(tag,{'X' 'Y'}));    tid = find(strmatch(fcn,DATA.TemplateLabels));    DATA.xyplot.xy{xydim} = fcn;    AllV.PlotOneXY(DATA,DATA.xyplot.xy);    set(DATA.toplevel,'UserData',DATA);    