function [res, C,D] = AutoCutOne(DATA, exname, ispk, args)    istart = now;    name = regexprep(exname, '\.p[0-9]*',sprintf('.p%d',ispk));    toplevel = DATA.toplevel;    if strcmp(DATA.autocutmode,'james')        [res,C,D] =  AllV.AllVPcs(name ,'nowatch',args{:},'tchan',ispk,'logfid',DATA.logfid,'automode','james','toplevel',toplevel);    elseif strcmp(DATA.autocutmode,'quick')        [res, C,D] =  AllV.AllVPcs(name ,'nowatch',args{:},'tchan',ispk,'logfid',DATA.logfid,'Expt',DATA.Expt,'quickauto','toplevel',toplevel);    else        [res, C,D] =  AllV.AllVPcs(name ,'nowatch',args{:},'tchan',ispk,'logfid',DATA.logfid,'saveautocut','toplevel',toplevel);    end    