function [Scores, T, details] = CalcScores(DATA, MeanSpike)% this need to be modified to work like AllV.TemplatePlot now, with%T being for all probes, but only calculating scores for chspk details.tstart = now;    AllVoltages = AllV.mygetappdata(DATA,'AllVoltages');if isfield(MeanSpike,'ms')    T = MeanSpike.ms;    if isfield(MeanSpike,'mu')        mT = MeanSpike.mu;    else        mT = T;    endelse    T = MeanSpike;endnpts = size(T,2);if isfield(MeanSpike,'othermeans')  && sum(size(MeanSpike.othermeans{1}) == size(MeanSpike.ms)) > 1    oT = MeanSpike.othermeans;else    oT{1} =T;    endfor j = 1:length(oT)    sz = size(oT{j});    if npts > sz(2)        oT{j}(:,sz(2):npts) = 0;    elseif npts > sz(2)        oT{j} = oT{j}(:,1:npts);    endend    if DATA.chspk(end) == DATA.allnprobes    else        dspk = [DATA.chspk DATA.chspk(end) + 1];    end    if max(DATA.chspk) >= DATA.allnprobes        dspk = [DATA.allnprobes-length(DATA.chspk):1:DATA.allnprobes];        csdspk = [DATA.allnprobes-length(DATA.chspk)-1:1:DATA.allnprobes];    elseif min(DATA.chspk) <= 1        dspk = [DATA.chspk DATA.chspk(end) + 1];        csdspk = [DATA.chspk DATA.chspk(end) + 1 DATA.chspk(end) + 2];    else        dspk = [DATA.chspk DATA.chspk(end) + 1];        csdspk = [DATA.chspk(1) - 1 DATA.chspk DATA.chspk(end) + 1];    end    dspk = dspk(dspk > 0 & dspk <= DATA.allnprobes);    csdspk = csdspk(csdspk > 0 & csdspk <= DATA.allnprobes);        if DATA.usestdtemplates        j = DATA.probe(1);        ispk = j;        meanV = repmat(mean(AllVoltages(j,:,:),2),[1  size(AllVoltages,2) 1]);        Scores(1,1,:) = MeanSpike(1,:) * squeeze(AllVoltages(j,:,:) - meanV);        Scores(1,2,:) = MeanSpike(2,:) * squeeze(AllVoltages(j,:,:) - meanV);        Scores(1,3,:) = squeeze(diff(DATA.StdTemplate(1,:),1,2)) * squeeze(diff(AllVoltages(j,:,:),1,2));        Scores(1,4,:) = squeeze(diff(DATA.StdTemplate(2,:),1,2)) * squeeze(diff(AllVoltages(j,:,:),1,2));        Scores(1,5,:) = squeeze(diff(DATA.StdTemplate(2,:),1,2)) * squeeze(diff(AllVoltages(j,:,:),1,2));        return;    end    if size(AllVoltages,1) > 2        if size(AllVoltages,1) < max(csdspk) && DATA.loadfromspikes            AllVoltages(max(csdspk),end,end) = 0;        end    dv = diff(AllVoltages(dspk,:,:),1,1);    mdv = diff(T(dspk,:,:),1,1);    csd = diff(AllVoltages(csdspk,:,:),2,1);    mcsd = diff(T(csdspk,:,:),2,1);    end%    meanV = repmat(mean(AllVoltages(DATA.chspk,:,:),2),[1  size(AllVoltages,2) 1]);    vid = 1:min([size(T,2) size(AllVoltages,2)]); %% in case old cut used different range    %but using indices makes calculatins slower, so only do this if    %necessary.    if size(T,2) ~= size(AllVoltages,2)        sizediff = 1;    else        sizediff = 0;    end%does meanV need to know about vid?        for j = 1:length(DATA.chspk)        c = DATA.chspk(j);        meanV = repmat(squeeze(mean(AllVoltages(c,:,:),2))',[size(AllVoltages,2) 1]);        AllVs{j} = squeeze(AllVoltages(c,:,:)) - meanV;    end        Scores = zeros(length(DATA.chspk),7+length(oT),DATA.nevents);    for j = 1:length(DATA.chspk)        c = DATA.chspk(j);        if size(AllVoltages,1) > 2        Scores(j,3,:) = squeeze(mdv(j,:)) * squeeze(dv(j,:,:));        Scores(j,4,:) = squeeze(mcsd(j,:)) * squeeze(csd(j,:,:));        end        if sizediff            sV = T(c,vid) - mean(T(c,vid));            mV = mT(c,vid) - mean(mT(c,vid));            Scores(j,2,:) = squeeze(diff(T(c,vid),1,2)) * squeeze(diff(AllVoltages(c,vid,:),1,2));            for k = 1:length(oT)                oV = oT{k}(c,vid) - mean(oT{k}(c,vid));                Scores(j,k+7,:) =  squeeze(oV) * AllVs{j};            end        else            sV = T(c,:) - mean(T(c,:)); %mean spike, probe c            mV = mT(c,:) - mean(mT(c,:));            Scores(j,2,:) = squeeze(diff(T(c,:),1,2)) * squeeze(diff(AllVoltages(c,:,:),1,2));            for k = 1:length(oT)                oV = squeeze(oT{k}(c,:) - mean(oT{k}(c,:)));                Scores(j,k+7,:) =  oV * AllVs{j};            end        end        Scores(j,1,:) = squeeze(sV) * AllVs{j};        Scores(j,6,:) =  squeeze(mV) * AllVs{j};        Scores(j,7,:) = sum(abs(repmat(sV',1,size(AllVoltages,3)) - AllVs{j}));        if size(MeanSpike.vdprime,1) >= c        dp = MeanSpike.vdprime(c,:);        Scores(j,5,:) = dp * squeeze(AllVoltages(c,:,:) - repmat(mV,[1 1 DATA.nevents]));        else        Scores(j,5,:) = 0;        end    end    if length(DATA.chspk) == 1        meanV = repmat(mean(AllVoltages(j,:,:),2),[1  size(AllVoltages,2) 1]);        Scores(1,3,:) = DATA.StdTemplate(1,:) * squeeze(AllVoltages(DATA.chspk,:,:) - meanV);        Scores(1,4,:) = DATA.StdTemplate(2,:) * squeeze(AllVoltages(DATA.chspk,:,:) - meanV);        Scores(1,6,:) = MeanSpike.mu(DATA.chspk,:) * squeeze(AllVoltages(DATA.chspk,:,:) - meanV);;        Scores(1,7,:) = DATA.rV;        Scores(1,8,:) = squeeze(diff(DATA.StdTemplate(1,:),1,2)) * squeeze(diff(AllVoltages(j,:,:),1,2));    end    if 0 %old way. Might want this back. Now store all channels (so can use CSD of template)        T = T(DATA.chspk,:);    end    details.endtime = now;