function [true, cluster] = ClusterIsSet(C, cl)%check that cluster cl is defined for a give clusterstruct    true = 0;    cluster = C;    if cl == 1        cluster = C;        if isfield(C,'space')            true = 1;        end    elseif ~isfield(C,'next') && isfield(C,'cluster') && C.cluster == cl        cluster = C;        true = 1;    elseif isfield(C,'next') && length(C.next) >= cl-1 && isfield(C.next{cl-1},'space')        true = 1;        cluster = C.next{cl-1};    end       