function [x,y] = GetClusterXYData(DATA, p)    C = AllV.GetClusterDef(DATA.cluster,DATA.currentcluster);    if isfield(C,'space')        usespace = C.space(1);    else        usespace = DATA.plottype;    end    if isempty(p) %Var/E plot        y = DATA.spkvar(DATA.probe(1),DATA.uid)'./DATA.energy(1,DATA.uid)';        x = DATA.energy(1,DATA.uid)';    elseif usespace == 2 %voltage pairs        AllVoltages = AllV.GetDataStruct(DATA, 'AllVoltages');        x = AllVoltages(p(1),p(2),DATA.uid);        y = AllVoltages(p(3),p(4),DATA.uid);    elseif ismember(usespace, [3 4 7])        x = DATA.TemplateScores(DATA.uid,p(1));        y = DATA.TemplateScores(DATA.uid,p(2));    else        x = DATA.pcs(DATA.uid,p(1));        y = DATA.pcs(DATA.uid,p(2));    endx = squeeze(x);y = squeeze(y);