function HistKeyPressed(src, ks)DATA = GetDataFromFig(src);spk = [0 0];changed = 1;if ~isfield(DATA,'cboundary')    return;endif strmatch(ks.Key,'rightarrow')    if DATA.cboundary.shape == 2        DATA.xy{1} = xyrotate(DATA.xy{1}(:,1),DATA.xy{1}(:,2),5 * pi/180);    else        DATA.cboundary.pos = AllV.RotateLine(DATA.cboundary.pos,5 * pi/180);        if isfield(DATA.cboundary,'axis')            axes(DATA.cboundary.axis);        end    end    DATA.cboundary = AllV.PlotHistogram(DATA,DATA.cboundary,'plotgmdetails');    AllV.SetFigure(DATA.tag.hist, DATA,DATA.watcharg{:});elseif strmatch(ks.Key,'leftarrow')    if DATA.cboundary.shape == 2        DATA.xy{1} = xyrotate(DATA.xy{1}(:,1),DATA.xy{1}(:,2),-5 * pi/180);    else        DATA.cboundary.pos = AllV.RotateLine(DATA.cboundary.pos,-5 * pi/180);        if isfield(DATA.cboundary,'axis')            axis(DATA.cboundary.axis);        end    end%    DATA.cboundary = AllV.PlotHistogram(DATA,DATA.cboundary,'plotgmdetails');    DATA.cboundary = AllV.PlotHistogram(DATA,DATA.cboundary);    AllV.SetFigure(DATA.tag.hist, DATA,DATA.watcharg{:});elseif strmatch(ks.Key,'d')    DATA.cboundary = AllV.PlotHistogram(DATA,DATA.cboundary,'plotgmdetails');else    changed = 0;endif changedset(DATA.toplevel,'UserData',DATA);end