function DATA = NextPCs(DATA)       DATA.hidecluster = 2;    DATA.currentcluster = 2;    AllVoltages = AllV.mygetappdata(DATA,'AllVoltages');    cid = find(DATA.clst == DATA.hidecluster);    DATA.uid = find(DATA.clst ~= DATA.hidecluster & DATA.clst > 0);    [C, DATA.Evec, pcs, dip, chspk, errs, DATA.pcfit] = AllV.CalcPCs(DATA,AllVoltages,DATA.nprobepc);    DATA.pcs(DATA.uid,:) = pcs;    id = setdiff(1:length(DATA.clst),DATA.uid);    DATA.clst(cid) = -DATA.hidecluster;    DATA = AllV.ReplotPCs(DATA,[]);    