function PlotCluster(a,b, mode)DATA = GetDataFromFig(a);DataClusters = AllV.mygetappdata(DATA,'Clusters');if mode == 1    PlotSpikeTimes(DataClusters);elseif mode == 2    PlotSpikeTimes(DataClusters,'xcorr');elseif mode == 3    PlotSpikeTimes(DataClusters,'xcorrstep',1);elseif mode == 4    PlotSpikeTimes(DataClusters,'xcorrstep',2);elseif mode == 5    PlotSpikeTimes(DataClusters,'dips');elseif mode == 6    PlotSpikeTimes(DataClusters,'probequality');end