function DATA = ReadFromLog(DATA)    ClusterLog = {};        logfile = sprintf('%s/ClusterLogExpt%d.mat',DATA.name,DATA.exptno);    if exist(logfile,'file')        load(logfile);        probeids = CellToMat(ClusterLog,'probe');        exptids = CellToMat(ClusterLog,'exptno');        reclusters = squeeze(CellToMat(ClusterLog,'recluster'))';        id = find(probeids == DATA.probe & reclusters(1,:) == 0 & exptids == DATA.exptno )        cluster = DATA.cluster;        for j = length(id):-1:1            C = ClusterLog{id(j)};            f = {'crit' 'shape' 'space' 'xyr' 'angle',};            DATA.currentcluster = 1;            for j = 1:length(f)                if isfield(C,f{j})                    cluster.(f{j}) = C.(f{j});                end            end            [a,b] = AllV.ClassifySpikes(DATA, cluster);            [c, d] = Counts(DATA.oldclst(a.id));            [e,f] = max(c);            fprintf('%s seems like C%d\n',datestr(C.savetime),d(f));        end    end            