function DATA = RestrictTimeRange(DATA, t)   DATA.uid = find(DATA.t >= t(1) & DATA.t <= t(2));    DATA.restricttimerange = t;    if isfield(DATA,'clid')       DATA.clid = DATA.clid(ismember(DATA.clid,DATA.uid));       DATA.nid = DATA.nid(ismember(DATA.nid,DATA.uid));    end    id = find([DATA.Expt.Trials.TrialStart] < t(1).*10000 | [DATA.Expt.Trials.TrialStart] > t(end).*10000);    DATA.excludetrialids = [DATA.Expt.Trials(id).id];    set(DATA.toplevel,'UserData',DATA);    AllV.SetTrialList(DATA);