function SetGUI(DATA)    if DATA.interactive < 0        return;    end    onoff = {'off' 'on'};    c = get(DATA.toplevel,'Children');    SetMenuCheck(DATA.tag.spikes,'PlotByTrial',DATA.plotspk.bytrial);    SetMenuCheck(DATA.toplevel,'PlotISI',DATA.plot.isi);    SetMenuCheck(DATA.toplevel,'PlotExpt',DATA.plot.expt);    SetMenuCheck(DATA.toplevel,'advanceprobe',DATA.auto.advanceprobe);    SetMenuCheck(DATA.toplevel,'checkxcorr',DATA.auto.checkxcorr);    SetMenuCheck(DATA.toplevel,'csd',DATA.csd > 0);    SetMenuCheck(DATA.toplevel,'dvdy',DATA.csd == 1);    SetMenuCheck(DATA.toplevel,'dvdt',DATA.dvdt);    SetMenuCheck(DATA.toplevel,'ProbeModeMenu',DATA.probeswitchmode,'exclusive');