 function SetOption(a, b, type)     onoff = {'off' 'on'};     DATA = GetDataFromFig(a);     if nargin < 3         type = get(a,'tag');     end     DATA.(type) = ~DATA.(type);     set(a,'Checked',onoff{DATA.(type)+1});     if DATA.dvdy         DATA.csd = 1;     elseif DATA.csd         DATA.csd = 2;     end     if strcmp(type,'usegmcid')         AllV.PlotHistogram(DATA,[]);     elseif sum(strcmp(type,{'csd' 'dvdy' 'dvdt'}))         DATA = AllV.SetPCs(DATA, 1,0);     end     set(DATA.toplevel,'UserData',DATA);     