function DATA = SetPCs(DATA, replot, reapply)    AllVoltages = AllV.mygetappdata(DATA,'AllVoltages');    args = {};    if reapply        [DATA, Cd] = AllV.LoadClusterDetails(DATA, 'orauto');        if ~isempty(Cd) && isfield(Cd{DATA.probe},'Evec')            args = {args{:} 'Evec' Cd{DATA.probe}.Evec };        end    end    [C, DATA.Evec, pcs, dip, chspk, errs, DATA.pcfit] = AllV.CalcPCs(DATA, AllVoltages,DATA.nprobepc, args{:});    DATA.errs = {DATA.errs{:} errs{:}};    DATA.pcs(DATA.uid,1:size(pcs,2)) = pcs; %now done in CalcPcs    if replot    DATA = AllV.ReplotPCs(DATA,[]);    end