function SpikeButtonPressed(a,b)DATA = GetDataFromFig(a);        DataClusters = AllV.mygetappdata(DATA,'Clusters');xy = get(a,'currentpoint');npts = length(DATA.spts);col = round((xy(1,1)+DATA.spts(1))./npts);row = round(xy(1,2)./DATA.vsep)+1;if col >= 0 && col < DATA.nprobes && row > 0 && row <= 6;    p = row + col * 6;    x = DATA.spts + col * npts;    y = [DATA.vsep/2 -DATA.vsep/2] + (row-1) * DATA.vsep;    imagesc(x,y,DataClusters{p}.MeanSpike.ms,'ButtonDownFcn',{@AllV.HitImage,p});    plot(x([1 end]),[y(1)-(p-1) * DATA.vsep/(DATA.nprobes-1) y(1)-(p-1) * DATA.vsep/(DATA.nprobes-1)],'w--');    DATA.selectprobe(p) = 1;endset(DATA.toplevel,'UserData',DATA);