function XYplot(a,b,tag,fcn)    DATA = GetDataFromFig(a);    onoff = {'off' 'on'};    if strcmp(tag,'density')        DATA.xyplot.density = ~DATA.xyplot.density;        set(a,'Checked',onoff{1+DATA.xyplot.density});        set(DATA.toplevel,'UserData',DATA);        AllV.PlotOneXY(DATA,DATA.xyplot.xy,'default');        return;    elseif strcmp(tag,'none')        DATA.xyplot.space(1) = 0;    end    xydim = find(strcmp(tag,{'X' 'Y'}));    tid = find(strmatch(fcn,DATA.TemplateLabels));    DATA.xyplot.xy{xydim} = fcn;    if ~isempty(tid)        DATA.xyplot.id(xydim) = tid(1);        DATA.xyplot.space(xydim) = 3;    end    AllV.PlotOneXY(DATA,DATA.xyplot.xy,'default');    set(DATA.toplevel,'UserData',DATA);    