function DATA = AddError(DATA, varargin) %prints an error and adds it to DATA struct%DATA = AddError(DATA, varargin)% passes varargin to sprintf, printf the resulting string in red, and adds% it to a cell string array DATA.errs    s = sprintf(varargin{:});    s = strrep(s,'\','\\');    s = strrep(s,'\\\\','\\');    mycprintf('red',s);    fprintf('\n');    if ~isfield(DATA,'errs')        DATA.errs = {};    end    DATA.errs = {DATA.errs{:} s};        